mapper.update:
update Broadcast
set BroadcastEndpoint = #{broadcastEndpoint,jdbcType=NVARCHAR},
  BroadcastName = #{broadcastName,jdbcType=NVARCHAR}
where BroadcastID = #{broadcastID,jdbcType=INTEGER}

mapper.select:
select BroadcastID, BroadcastEndpoint, BroadcastName
from Broadcast
where BroadcastID = #{broadcastID,jdbcType=INTEGER}
