mapper.update:
update CtrlDetails
set CtrlRecordID = #{ctrlRecordID,jdbcType=INTEGER},
  Time = #{time,jdbcType=TIMESTAMP},
  Record = #{record,jdbcType=NVARCHAR}
where ID = #{ID,jdbcType=INTEGER}

mapper.select:
select ID, CtrlRecordID, Time, Record
from CtrlDetails
where ID = #{ID,jdbcType=INTEGER}

select ID, CtrlRecordID, Time, Record
from CtrlDetails
where CtrlRecordID = #{ctrlRecordID}
