mapper.update:
update LoginRecord
set UserID = #{userID,jdbcType=INTEGER},
  UserName = #{userName,jdbcType=NVARCHAR},
  Time = #{time,jdbcType=TIMESTAMP},
  IP = #{IP,jdbcType=NVARCHAR}
where ID = #{ID,jdbcType=INTEGER}

mapper.select:
select ID, UserID, UserName, Time, IP
from LoginRecord
where ID = #{ID,jdbcType=INTEGER}
