mapper.update:
update Role
set RoleName = #{roleName,jdbcType=NVARCHAR},
  CreateTime = #{createTime,jdbcType=TIMESTAMP},
  Description = #{description,jdbcType=NVARCHAR}
where ID = #{ID,jdbcType=INTEGER}

mapper.select:
select ID, RoleName, CreateTime, Description
from Role
where ID = #{ID,jdbcType=INTEGER}
